import React, { useEffect, useState } from "react";
import { Formik, Field, Form, ErrorMessage } from "formik";
import { apiUrl } from "../App";
import * as Yup from "yup";
import axios from "axios";

// Components :
import NavBar from "./NavBar";

function AddDocket() {
  const [poDetails, setPoDetails] = useState();
  const docketDetails = {
    name: "",
    startTime: "",
    endTime: "",
    hoursWorked: "",
    ratePerHour: "",
    supplierName: "",
    poNumber: "",
  };

  // ValidationSchema
  const docketSchema = Yup.object({
    name: Yup.string().min(3).max(25).required("Name is required"),
    startTime: Yup.string().required("Start Time is required"),
    endTime: Yup.string().required("End Time is required"),
    hoursWorked: Yup.string().required("No of Hours Worked is required"),
    ratePerHour: Yup.string().required("Rate Per Hour is required"),
    // supplierName: Yup.string().required("Supplier Name is required"),
    // poNumber: Yup.string().required("P.O Number is required"),
  });

  // Get Supplier and PO Details:
  const getPoDetails = async (setPoDetails) => {
    try {
      const res = await axios.post(`${apiUrl}/getPoDetails`);
      if (res.status == 200) {
        setPoDetails(res.data.result);
      }
    } catch (error) {
      console.log("error: ", error);
    }
  };

  useEffect(() => {
    getPoDetails(setPoDetails);
  }, []);

  console.log("poDetails: ", poDetails);

  const poDetailsOption = [{ label: "", value: "" }];
  const createPoDetails = poDetails.map((e) => {
    let object = { label: e.poNumber, value: e.poNumber };
    poDetailsOption.push(object);
  });
  console.log("poDetailsOption: ", poDetailsOption);

  // Handle form submission
  const handleSubmit = (values) => {
    console.log("Form values:", values);
  };
  return (
    <div className="flex flex-col">
      <NavBar />
      <div className="bg-[#F3F4F6] mt-5 ml-5 w-[25em] p-5 rounded-lg flex justify-center">
        <Formik
          initialValues={docketDetails}
          validationSchema={docketSchema}
          onSubmit={handleSubmit}
        >
          <Form>
            <div className="flex flex-col w-[22em]">
              <label htmlFor="name">Name</label>
              <Field
                type="text"
                id="name"
                name="name"
                className="bg-[#FFFFFF] h-[2em] rounded-sm mt-2"
              />
              <ErrorMessage
                name="name"
                component="p"
                className="text-[12px] text-red-500 pt-2"
              />
            </div>

            <div className="flex flex-col pt-3">
              <label htmlFor="startTime">Start Time</label>
              <Field
                type="text"
                id="startTime"
                name="startTime"
                className="bg-[#FFFFFF] h-[2em] rounded-sm mt-2"
              />
              <ErrorMessage
                name="startTime"
                component="div"
                className="text-[12px] text-red-500 pt-2"
              />
            </div>

            <div className="flex flex-col pt-3">
              <label htmlFor="endTime">End Time</label>
              <Field
                type="text"
                id="endTime"
                name="endTime"
                className="bg-[#FFFFFF] h-[2em] rounded-sm mt-2"
              />
              <ErrorMessage
                name="endTime"
                component="div"
                className="text-[12px] text-red-500 pt-2"
              />
            </div>

            <div className="flex flex-col pt-3">
              <label htmlFor="hoursWorked">No of Hours Worked</label>
              <Field
                type="text"
                id="hoursWorked"
                name="hoursWorked"
                className="bg-[#FFFFFF] h-[2em] rounded-sm mt-2"
              />
              <ErrorMessage
                name="hoursWorked"
                component="div"
                className="text-[12px] text-red-500 pt-2"
              />
            </div>

            <div className="flex flex-col pt-3">
              <label htmlFor="ratePerHour">Rate Per Hour</label>
              <Field
                type="text"
                id="ratePerHour"
                name="ratePerHour"
                className="bg-[#FFFFFF] h-[2em] rounded-sm mt-2 pl-2 text-red-500"
              />
              <ErrorMessage
                name="ratePerHour"
                component="div"
                className="text-[12px] text-red-500 pt-2"
              />
            </div>

            <div className="flex flex-col pt-3">
              <label htmlFor="supplierName">Supplier Name</label>
              <select
                id="supplierName"
                name="supplierName"
                className="bg-[#FFFFFF] h-[2em] rounded-sm mt-2"
              >
                <option value="" disabled>
                  Select an option
                </option>
                <option value="supplier">Supplier Name</option>
                <option value="poNumber">PO Number</option>
              </select>
              <ErrorMessage
                name="supplierName"
                component="div"
                className="text-[12px] text-red-500 pt-2"
              />
            </div>

            <div className="flex flex-col pt-3">
              <label htmlFor="poNumber">P.O Number</label>
              <select
                id="poNumber"
                name="poNumber"
                className="bg-[#FFFFFF] h-[2em] rounded-sm mt-2"
              >
                <option value="" disabled>
                  Select an option
                </option>
                <option value="supplier">Supplier Name</option>
                <option value="poNumber">PO Number</option>
              </select>
              <ErrorMessage
                name="poNumber"
                component="div"
                className="text-[12px] text-red-500 pt-2"
              />
            </div>

            <button
              type="submit"
              className="bg-[#16174F] text-[#FFFFFF] px-4 py-2 rounded-sm mt-5"
            >
              Submit
            </button>
          </Form>
        </Formik>
      </div>
    </div>
  );
}

export default AddDocket;

{
  /* <TextInput name="Name" validation="name" />
            <TextInput name="Start Time" validation="startTime" />
            <TextInput name="End Time" validation="endTime" />
            <TextInput name="No of Hours Worked" validation="hoursWorked" />
            <TextInput name="Rate Per Hour" validation="ratePerHour" /> */
}
