import React from "react";
import { Formik, Field, Form, ErrorMessage } from "formik";
import * as Yup from "yup";

// Components :
import NavBar from "./NavBar";

function AddDocket() {
  const initialValues = {
    name: "",
    startTime: "",
    endTime: "",
    hoursWorked: "",
    ratePerHour: "",
  };

  // Define validation schema using Yup
  const validationSchema = Yup.object({
    name: Yup.string().min(3).max(10).required("Name is required"),
    startTime: Yup.string().required("Start Time is required"),
    endTime: Yup.string().required("End Time is required"),
    hoursWorked: Yup.string().required("No of Hours Worked is required"),
    ratePerHour: Yup.string().required("Rate Per Hour is required"),
  });

  // Handle form submission
  const handleSubmit = (values) => {
    // You can perform actions with the form values here
    console.log("Form values:", values);
  };
  return (
    <div className="flex flex-col">
      <NavBar />
      <div className="bg-[#F3F4F6] mt-5 w-[25em] p-5 rounded-lg flex justify-center">
        <Formik
          initialValues={initialValues}
          validationSchema={validationSchema}
          onSubmit={handleSubmit}
        >
          <Form>
            <div className="flex flex-col">
              <label htmlFor="name">Name</label>
              <Field type="text" id="name" name="name" className="bg-red-300" />
              <ErrorMessage name="name" component="div" className=""/>
            </div>

            <div className="flex flex-col pt-3">
              <label htmlFor="startTime" className="">Start Time</label>
              <Field type="text" id="startTime" name="startTime" className="mt-2" />
              <ErrorMessage name="startTime" component="div" className="mt-2" />
            </div>

            <div>
              <label htmlFor="endTime">End Time</label>
              <Field type="text" id="endTime" name="endTime" />
              <ErrorMessage name="endTime" component="div" />
            </div>

            <div>
              <label htmlFor="hoursWorked">No of Hours Worked</label>
              <Field type="text" id="hoursWorked" name="hoursWorked" />
              <ErrorMessage name="hoursWorked" component="div" />
            </div>

            <div>
              <label htmlFor="ratePerHour">Rate Per Hour</label>
              <Field type="text" id="ratePerHour" name="ratePerHour" />
              <ErrorMessage name="ratePerHour" component="div" />
            </div>

            <button type="submit">Submit</button>
          </Form>
        </Formik>
      </div>
    </div>
  );
}

export default AddDocket;
